from datetime import datetime
import random

class Player:
    def __init__(self, name, number):
        self.name = name
        self.number = number
        self.turn = True  # Track if it's this player's turn

    def take_turn(self):
        if self.turn:
            print(self.name + " takes turn.")
            self.tic_tac_toe()
            self.toggle_turn()
        else:
            print("It is not " + self.name + "'s turn."

    def toggle_turn(self):
        self.turn = not self.turn

class TicTacToe:
    def __init__(self):
        self.board = [' ' for _ in range(9)]
        self.current_winner = None

    def print_board(self):
        for row in [self.board[i*3:(i+1)*3] for i in range(3)]:
            print('| ' + ' | '.join(row) + ' |')

    def available_moves(self):
        return [i for i, x in enumerate(self.board) if x == " "]

    def empty_squares(self):
        return " " in self.board

    def num_empty_squares(self):
        return self.board.count(" ")

    def make_move(self, square, letter):
        if self.board[square] == " ":
            self.board[square] = letter
            if self.winner(square, letter):
                self.current_winner = letter
            return True
        return False

    def winner(self, square, letter):
        row_ind = square // 3
        row = self.board[row_ind*3:(row_ind+1)*3]
        if all([spot == letter for spot in row]):
            return True
        col_ind = square % 3
        column = [self.board[col_ind+i*3] for i in range(3)]
        if all([spot == letter for spot in column]):
            return True
        if square % 2 == 0:
            diagonal1 = [self.board[i] for i in [0, 4, 8]]
            if all([spot == letter for spot in diagonal1]):
                return True
            diagonal2 = [self.board[i] for i in [2, 4, 6]]
            if all([spot == letter for spot in diagonal2]):
                return True
        return False

    def get_player_move(self, letter):
        available_moves = self.available_moves()
        move = None
        while move not in available_moves:
            try:
                move = int(input(f"{letter}, enter a move: "))
            except ValueError:
                continue
        return move

    def play(self, game_over):
        if game_over:
            return False
        else:
            if self.current_turn == 'X':
                index = self.get_player_move(self.current_turn)
                if (self.make_move(index, self.current_turn)):
                    self.play(self.winner(self, index))
                else:
                    self.play(self.winner(index))
            else:
                return self.current_turn
                self.current_turn = 'X'

